import math
import sys

def main():
    input = sys.stdin.readline
    n, k = map(int, input().split())

    habitats = []
    for _ in range(n):
        x, y = map(int, input().split())
        habitats.append((x, y))

    def check(r):
        count = 0
        for x, y in habitats:
            if (x**2 + y**2) <= r**2:
                count += 1
        return count >= k

    left, right = 0, 2 * 10**5
    while right - left > 10**-8:
        mid = (left + right) / 2
        if check(mid):
            right = mid
        else:
            left = mid

    print(format(right, ".8f"))

if __name__ == "__main__":
    main()
