def main():
    n, m = map(int, input().split())
    a = input()
    b = input()

    # Precompute the longest prefix that is also a suffix for each substring of b
    lps = [0]*m
    j = 0
    for i in range(1, m):
        while j > 0 and b[i] != b[j]:
            j = lps[j-1]
        if b[i] == b[j]:
            j += 1
        lps[i] = j

    # Precompute the KMP table for a and b
    kmp = [0]*n
    j = 0
    for i in range(n):
        while j > 0 and a[i] != b[j]:
            j = lps[j-1]
        if a[i] == b[j]:
            j += 1
        kmp[i] = j

    # Compute the number of occurrences of b in a
    occurrences = []
    last_occurrence = -1
    for i in range(n):
        if kmp[i] == m:
            occurrences.append(i-m+1)
            last_occurrence = i

    # Initialize the dynamic programming table
    dp = [[float('inf')]*(len(occurrences)+2) for _ in range(n+1)]
    dp[0][0] = 0

    # Fill in the dynamic programming table
    for i in range(n):
        for k in range(len(occurrences)+1):
            # If no change is made
            dp[i+1][k] = min(dp[i+1][k], dp[i][k] + (a[i] != '0'))

            # If a change is made
            if k < len(occurrences) and i == occurrences[k]:
                dp[i+1][k+1] = min(dp[i+1][k+1], dp[i][k] + (a[i] != b[-1]))

    # Compute the minimum number of changes for each k
    for k in range(len(occurrences)+2):
        min_changes = float('inf')
        for i in range(m-1, n+1):
            min_changes = min(min_changes, dp[i][k])
        if min_changes == float('inf'):
            min_changes = -1
        print(min_changes, end=' ' if k < len(occurrences)+1 else '\n')

if __name__ == "__main__":
    main()
