import sys
input = sys.stdin.readline

class SegmentTree:
    def __init__(self, size, arr):
        self.n = size
        self.tree = [0] * (4 * size)
        self.arr = arr
        self.build(0, 0, self.n - 1)

    def build(self, node, start, end):
        if start == end:
            self.tree[node] = self.arr[start]
        else:
            mid = (start + end) // 2
            self.build(2 * node + 1, start, mid)
            self.build(2 * node + 2, mid + 1, end)
            self.tree[node] = max(self.tree[2 * node + 1], self.tree[2 * node + 2])

    def update(self, node, start, end, idx, val):
        if start == end:
            self.tree[node] = val
        else:
            mid = (start + end) // 2
            if idx <= mid:
                self.update(2 * node + 1, start, mid, idx, val)
            else:
                self.update(2 * node + 2, mid + 1, end, idx, val)
            self.tree[node] = max(self.tree[2 * node + 1], self.tree[2 * node + 2])

    def query(self, node, start, end, l, r):
        if r < start or l > end:
            return float('-inf')
        if l <= start and end <= r:
            return self.tree[node]
        mid = (start + end) // 2
        p1 = self.query(2 * node + 1, start, mid, l, r)
        p2 = self.query(2 * node + 2, mid + 1, end, l, r)
        return max(p1, p2)

def main():
    n, q = map(int, input().split())
    a = list(map(int, input().split()))

    adj = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        adj[u].append(v)
        adj[v].append(u)

    order = []
    parent = [0] * (n + 1)
    depth = [0] * (n + 1)
    stack = [(1, 0)]
    while stack:
        node, p = stack.pop()
        order.append(node)
        parent[node] = p
        for nei in adj[node]:
            if nei != p:
                depth[nei] = depth[node] + 1
                stack.append((nei, node))

    tm = [[] for _ in range(n + 1)]
    leaf = []
    for i in range(n, 0, -1):
        x = order[i - 1]
        if len(adj[x]) == 1:
            leaf.append(x)
        else:
            for nei in adj[x]:
                if parent[nei] == x:
                    tm[x].append(nei)

    dp = [0] * (n + 1)
    for x in leaf:
        dp[x] = a[x - 1]

    for i in range(n - 1, 0, -1):
        x = order[i - 1]
        tmp = []
        for y in tm[x]:
            tmp.append(dp[y])
        dp[x] = max(abs(sum(tmp)), max(abs(a[x - 1] + sum(tmp)), abs(a[x - 1] - sum(tmp))))

    st = SegmentTree(n, dp)
    ans = []
    for _ in range(q):
        arr = list(map(int, input().split()))
        if arr[0] == 1:
            u, c = arr[1:]
            a[u - 1] = c
            dp[u] = c
            st.update(0, 0, n - 1, u - 1, c)
            for x in tm[u]:
                dp[x] = max(abs(sum([dp[y] for y in tm[x]])), max(abs(a[x - 1] + sum([dp[y] for y in tm[x]])), abs(a[x - 1] - sum([dp[y] for y in tm[x]]))))
                st.update(0, 0, n - 1, x - 1, dp[x])
        else:
            u, v = arr[1:]
            if u == v:
                ans.append(0)
            elif depth[u] < depth[v]:
                u, v = v, u
            tmp = []
            while depth[u] > depth[v]:
                tmp.append(dp[u])
                u = parent[u]
            tmp.append(dp[u])
            while u != v:
                tmp.append(dp[u])
                tmp.append(dp[v])
                u = parent[u]
                v = parent[v]
            ans.append(max(abs(sum(tmp)), max(abs(sum(tmp) + a[u - 1]), abs(sum(tmp) - a[u - 1]))))

    for a in ans:
        print(a)

main()
