def main():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))

    # Initialize the minimum value table
    min_val = [[0]*n for _ in range(n)]
    for i in range(n):
        min_val[i][i] = a[i]
    for length in range(1, n):
        for i in range(n - length):
            j = i + length
            min_val[i][j] = min(min_val[i][j-1], a[j])

    # Initialize the dp table
    dp = [[-float('inf')]*m for _ in range(n)]
    for i in range(n):
        dp[i][0] = m * a[i]

    # Calculate the dp table
    for length in range(1, m):
        for i in range(n):
            if i < length:
                continue
            for j in range(i - length, -1, -1):
                sum_val = 0
                for k in range(j, i + 1):
                    sum_val += dp[k][length - 1]
                sum_val -= (i - j + 1) * (2 * length - 1) * min_val[j][i]
                dp[i][length] = max(dp[i][length], sum_val)

    # Find the maximum value
    max_val = -float('inf')
    for i in range(n):
        max_val = max(max_val, dp[i][m - 1])

    print(max_val)

