import sys
import heapq
input = sys.stdin.readline

def main():
    n, m, q = map(int, input().split())
    w = [0] + list(map(int, input().split()))
    g = [[] for _ in range(n+1)]
    for _ in range(m):
        u, v, d = map(int, input().split())
        g[u].append((v, d))
        g[v].append((u, d))

    fa = [i for i in range(n+1)]
    def find(x):
        if fa[x] != x:
            fa[x] = find(fa[x])
        return fa[x]

    dis = [float('inf')] * (n+1)
    dis[1] = 0
    pq = [(0, 1)]
    v = [0] * (n+1)
    while pq:
        d, u = heapq.heappop(pq)
        if v[u]:
            continue
        v[u] = 1
        for v, d2 in g[u]:
            if dis[v] > dis[u] + d2:
                dis[v] = dis[u] + d2
                heapq.heappush(pq, (dis[v], v))

    for i in range(2, n+1):
