    n, q = map(int, input().split())
    e = list(map(int, input().split()))
    graph = [[] for _ in range(n)]
    for _ in range(n - 1):
        a, b, c, t = map(int, input().split())
        a -= 1
        b -= 1
        graph[a].append((b, c, t))
        graph[b].append((a, c, t))

    thres = 30
    for i in range(thres):
        g = [[] for _ in range(n)]
        for j in range(n):
            for k, c, t in graph[j]:
                if c >> i & 1:
                    g[j].append((k, t))
        mx_e, mx_t = [0] * n, [0] * n
        for j in range(n):
            if g[j]:
                h = [(0, j)]
                vis = [0] * n
                vis[j] = 1
                while h:
                    t, x = heapq.heappop(h)
                    if mx_e[x] < e[x]:
                        mx_e[x], mx_t[x] = e[x], t
                    for k, t2 in g[x]:
                        if not vis[k]:
                            vis[k] = 1
                            heapq.heappush(h, (max(t, t2), k))
        e = mx_e
        graph = g

    for _ in range(q):
