def main():
    n = int(input())
    grid = [list(input()) for _ in range(n)]

    # Step 1: Initialize the directions array
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    # Step 2: Function to check if a position is valid
    def is_valid(i, j):
        return 0 <= i < n and 0 <= j < n

    # Step 3: Function to check if a character is valid
    def is_valid_character(c):
        return c == 'G' or c == 'S'

    # Step 4: Function to get the opposite color
    def get_opposite_color(color):
        if color == 'G':
            return 'S'
        else:
            return 'G'

    # Step 5: Function to perform DFS
    def dfs(i, j, color):
        if grid[i][j] != '.':
            if grid[i][j] != color:
                return False
            return True
        grid[i][j] = color
        for di, dj in directions:
            ni, nj = i + di, j + dj
            if not is_valid(ni, nj) or not dfs(ni, nj, get_opposite_color(color)):
                return False
        return True

    # Step 6: Try to fill in all the cells
    ans = None
    for i in range(n):
        for j in range(n):
            if grid[i][j] == '.':
                grid[i][j] = 'G'
                if dfs(i, j, 'G'):
                    ans = grid
                grid[i][j] = 'S'
                if dfs(i, j, 'S'):
                    if ans is None:
                        ans = grid
                    else:
                        print('MULTIPLE')
                        return
                grid[i][j] = '.'
    if ans is None:
        print('NONE')
        return
    print('UNIQUE')
    for row in ans:
        print(''.join(row))

if __name__ == "__main__":
