import math
def main():
    N, K = map(int, input().split())
    points = []
    for _ in range(N):
        points.append(list(map(int, input().split())))

    def dist(x1, y1, x2, y2):
        return math.sqrt((x1 - x2)**2 + (y1 - y2)**2)

    # Step 1: Find slope of line passing through two points
    def slope(x1, y1, x2, y2):
        if x2 - x1 == 0:
            return float('inf')
        return (y2 - y1) / (x2 - x1)

    # Step 2: Find the point that does not lie on the line
    slope_set = set()
    outlier = None
    for i in range(N - 1):
        for j in range(i + 1, N):
            m = slope(points[i][0], points[i][1], points[j][0], points[j][1])
            slope_set.add(m)
            if len(slope_set) > 2:
                outlier = points[j]
                break
        if outlier:
            break

    # Step 3: Sort the points on the line
    line_points = [point for point in points if point != outlier]
    line_points.sort()

    # Step 4: Calculate the distance to travel on the line
    line_dist = 0
    for i in range(len(line_points) - 1):
        line_dist += dist(line_points[i][0], line_points[i][1], line_points[i + 1][0], line_points[i + 1][1])

    # Step 5: Calculate the total distance
    start_point = points[K - 1]
    if start_point == outlier:
        total_dist = min(dist(outlier[0], outlier[1], line_points[0][0], line_points[0][1]) + line_dist,
                         dist(outlier[0], outlier[1], line_points[-1][0], line_points[-1][1]) + line_dist)
