            left = i
            right = i

            while left > 0:
                if arr[left - 1] <= curr:
                    left -= 1
                else:
                    break

            while right < n - 1:
                if arr[right + 1] >= curr:
                    right += 1
                else:
                    break

            for k in range(left + 1, i + 1):
                prev = curr
                curr = (prev + arr[k - 1] - 1) // arr[k - 1]
                res += curr * (k - left) * (right - k + 1)
                res %= mod

            res += (right - i + 1) * (i - left + 1) * curr
            res %= mod

        print(res)

