def main():
    n = int(input())
    edges = [list(map(int, input().split())) for _ in range(n - 1)]
    g = [[] for _ in range(n + 1)]
    for x, y in edges:
        g[x].append(y)
        g[y].append(x)

    q = int(input())
    queries = [list(map(int, input().split())) for _ in range(q)]

    # Step 1: dfs to calculate the number of children for each node
    ans = [-1] * q
    child_cnt = [0] * (n + 1)
    parent = [-1] * (n + 1)
    stack = [(1, -1)]
    while stack:
        node, p = stack.pop()
        parent[node] = p
        for child in g[node]:
            if child != p:
                stack.append((child, node))
                child_cnt[node] += 1

    # Step 2: dfs to calculate the number of grandchildren for each node
    grandchild_cnt = [0] * (n + 1)
    stack = [(1, -1)]
    while stack:
        node, p = stack.pop()
        for child in g[node]:
            if child != p:
                stack.append((child, node))
                grandchild_cnt[node] += child_cnt[child] - 1

    # Step 3: calculate the answer for each query
    for i, (v, k) in enumerate(queries):
        max_child_cnt = child_cnt[v]
        max_grandchild_cnt = grandchild_cnt[v]
