    t = int(input())
    for _ in range(t):
        n, m = map(int, input().split())
        grid = [input() for _ in range(n)]

        # Find the lab
        lab = None
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 'L':
                    lab = (i, j)

        # DFS to mark reachable cells
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        stack = [lab]
        while stack:
            x, y = stack.pop()
            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 0 <= nx < n and 0 <= ny < m and grid[nx][ny] == '.':
                    # Check if the cell has only one free neighbor
                    count = 0
                    for ddx, ddy in directions:
                        nnx, nny = nx + ddx, ny + ddy
                        if 0 <= nnx < n and 0 <= nny < m and grid[nnx][nny] == '.':
                            count += 1
                    if count <= 1:
                        grid[nx] = grid[nx][:ny] + '+' + grid[nx][ny+1:]
                        stack.append((nx, ny))

        # Print the modified grid
        for row in grid:
            print(row)

