            edges.append((x - 1, y - 1, v))
        for _ in range(m):
            a, b, p = map(int, input().split())
            elves.append((a - 1, b - 1, p))

        ds = DisjointSet(n)
        for x, y, v in edges:
            if v != -1:
                ds.union(x, y, v)

        for x, y, p in elves:
            if (count_bits(ds.values[x] ^ ds.values[y]) - count_bits(ds.values[x]) - count_bits(ds.values[y])) % 2 != p:
                print("NO")
