def main():
    import sys

    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    adj = [[] for _ in range(n)]
    deg = [0] * n
    for _ in range(m):
        u, v = map(int, input().split())
        u -= 1
        v -= 1
        adj[u].append(v)
        deg[v] += 1

    top_sort = []
    q = []
    for i in range(n):
        if deg[i] == 0:
            q.append(i)
    while q:
        u = q.pop()
        top_sort.append(u)
        for v in adj[u]:
            deg[v] -= 1
            if deg[v] == 0:
                q.append(v)

    assert len(top_sort) == n

    ans = []
    for i in range(n):
        if i in top_sort:
            ans.append(a[i])
        else:
            min_adj = sys.maxsize
            for v in adj[i]:
                min_adj = min(min_adj, ans[top_sort.index(v)])
            ans.append(min_adj)

    print(*ans)

if __name__ == "__main__":
