    choco_dict = {val: i for i, val in enumerate(a)}

    # Create a dictionary to store the number of exchanges needed to reach each chocolate type
    exchanges_dict = {val: 0 for val in a}

    # Iterate over each chocolate type to calculate the number of exchanges needed to reach it
    for choco in a:
        temp = choco
        while temp not in choco_dict:
            if temp == 0:
                break
            k = 0
            while (1 << k) < temp:
                k += 1
            exchanges_dict[choco] += 1
            temp = (1 << k) - temp

    # Initialize the maximum number of exchanges and the pair of chocolates that achieve it
    max_exchanges = 0
    pair = (0, 0)

    # Iterate over each pair of chocolates to find the pair with the maximum number of exchanges
    for i in range(n):
        for j in range(i + 1, n):
            temp_i = a[i]
            temp_j = a[j]
            exchanges = 0
            while temp_i != temp_j:
                if temp_i in exchanges_dict:
                    exchanges += exchanges_dict[temp_i]
                    temp_i = a[i]
