def main():
    from math import gcd
    from functools import reduce

    def solve(a):
        n = len(a)
        def get_gcd(arr):
            return reduce(gcd, arr)

        gcd1 = get_gcd(a[::2])
        gcd2 = get_gcd(a[1::2])

        def is_valid(d):
            for i in range(1, n):
                if (a[i] % d == 0 and a[i-1] % d == 0):
                    return False
            return True

        if is_valid(gcd1) and gcd1 != 1:
            return gcd1
        if is_valid(gcd2) and gcd2 != 1:
            return gcd2
        return 0

    t = int(input())
    for _ in range(t):
        n = int(input())
        a = list(map(int, input().split()))
        print(solve(a))

if __name__ == "__main__":
    main()
