def main():
    MOD = 998244353
    N = int(input())
    S = [input() for _ in range(N)]
    cnt = [[0] * 26 for _ in range(N)]
    for i, s in enumerate(S):
        for c in s:
            cnt[i][ord(c) - ord('a')] += 1

    ans = 0
    for i in range(1 << N):
        cnts = [0] * 26
        sum = 0
        for j in range(N):
            if i >> j & 1:
                sum += j + 1
                for k in range(26):
                    cnts[k] = max(cnts[k], cnt[j][k])
        f = 1
        for k in range(26):
            f = (f * (cnts[k] + 1)) % MOD
        ans ^= f * sum

    print(ans)

